// üìä COMPREHENSIVE CYBERSECURITY SUMMARY REPORT GENERATOR
console.log("üìä GENERATING COMPREHENSIVE SECURITY SUMMARY...");

const reportTimestamp = new Date().toISOString();
const reportDate = new Date().toLocaleDateString();
const reportTime = new Date().toLocaleTimeString();

// Initialize summary structure
const securitySummary = {
  // Report metadata
  report_info: {
    generated_at: reportTimestamp,
    report_date: reportDate,
    report_time: reportTime,
    report_type: 'AI_CYBERSECURITY_ANALYSIS',
    report_version: '2.0',
    generated_by: 'n8n_AI_Security_Agent'
  },

  // Executive summary
  executive_summary: {
    total_threats_analyzed: items.length,
    scan_duration: '30 seconds',
    ai_engine: 'Groq Llama3',
    threat_sources: ['IPSUM Feed', 'FireHOL', 'Real-time Intelligence']
  },

  // Action breakdown
  actions_taken: {
    critical_blocks: 0,
    high_risk_scheduled: 0,
    alerts_generated: 0,
    threats_monitored: 0
  },

  // Risk analysis
  risk_analysis: {
    highest_risk_score: 0,
    lowest_risk_score: 100,
    average_risk_score: 0,
    risk_distribution: {
      critical: 0,    // 90+
      high: 0,        // 70-89
      medium: 0,      // 50-69
      low: 0          // <50
    }
  },

  // Threat intelligence
  threat_intelligence: {
    threat_types: {},
    countries_involved: {},
    attack_vectors: {},
    ai_recommendations: {}
  },

  // Detailed findings
  detailed_findings: [],

  // Security recommendations
  recommendations: []
};

let totalRiskScore = 0;

// Process each threat item
for (const item of items) {
  const threat = item.json;

  console.log(`Processing: ${threat.ip} - ${threat.action_taken}`);

  // Count actions taken
  switch (threat.action_taken) {
    case 'BLOCKED_IMMEDIATELY':
      securitySummary.actions_taken.critical_blocks++;
      break;
    case 'SCHEDULED_FOR_BLOCKING':
      securitySummary.actions_taken.high_risk_scheduled++;
      break;
    case 'ALERT_GENERATED':
      securitySummary.actions_taken.alerts_generated++;
      break;
    case 'ADDED_TO_WATCHLIST':
      securitySummary.actions_taken.threats_monitored++;
      break;
  }

  // Risk analysis
  const riskScore = threat.final_risk_score || threat.ai_risk_score || 50;
  totalRiskScore += riskScore;

  if (riskScore > securitySummary.risk_analysis.highest_risk_score) {
    securitySummary.risk_analysis.highest_risk_score = riskScore;
  }
  if (riskScore < securitySummary.risk_analysis.lowest_risk_score) {
    securitySummary.risk_analysis.lowest_risk_score = riskScore;
  }

  // Risk distribution
  if (riskScore >= 90) securitySummary.risk_analysis.risk_distribution.critical++;
  else if (riskScore >= 70) securitySummary.risk_analysis.risk_distribution.high++;
  else if (riskScore >= 50) securitySummary.risk_analysis.risk_distribution.medium++;
  else securitySummary.risk_analysis.risk_distribution.low++;

  // Threat intelligence tracking
  if (threat.threat_type) {
    securitySummary.threat_intelligence.threat_types[threat.threat_type] = 
      (securitySummary.threat_intelligence.threat_types[threat.threat_type] || 0) + 1;
  }

  if (threat.geo_risk || threat.country) {
    const country = threat.geo_risk || threat.country || 'Unknown';
    securitySummary.threat_intelligence.countries_involved[country] = 
      (securitySummary.threat_intelligence.countries_involved[country] || 0) + 1;
  }

  if (threat.recommended_action) {
    securitySummary.threat_intelligence.ai_recommendations[threat.recommended_action] = 
      (securitySummary.threat_intelligence.ai_recommendations[threat.recommended_action] || 0) + 1;
  }

  // Store detailed findings
  securitySummary.detailed_findings.push({
    ip_address: threat.ip,
    risk_score: riskScore,
    threat_type: threat.threat_type || 'Unknown',
    action_taken: threat.action_taken,
    ai_analysis: threat.analysis_summary || 'No summary available',
    timestamp: threat.analysis_timestamp || reportTimestamp
  });
}

// Calculate averages
securitySummary.risk_analysis.average_risk_score = 
  Math.round(totalRiskScore / items.length);

// Generate recommendations based on findings
const recommendations = [];

if (securitySummary.actions_taken.critical_blocks > 0) {
  recommendations.push({
    priority: 'HIGH',
    category: 'Immediate Action',
    recommendation: `${securitySummary.actions_taken.critical_blocks} critical threats were blocked. Review firewall logs and validate blocking rules.`,
    timeline: 'Immediate'
  });
}

if (securitySummary.actions_taken.alerts_generated > 0) {
  recommendations.push({
    priority: 'MEDIUM',
    category: 'Investigation Required',
    recommendation: `${securitySummary.actions_taken.alerts_generated} threats require SOC team investigation within 2 hours.`,
    timeline: '2 hours'
  });
}

if (securitySummary.risk_analysis.average_risk_score > 70) {
  recommendations.push({
    priority: 'HIGH',
    category: 'Security Posture',
    recommendation: 'High average risk score detected. Consider enhancing threat intelligence feeds and security controls.',
    timeline: '24 hours'
  });
}

recommendations.push({
  priority: 'LOW',
  category: 'Continuous Improvement',
  recommendation: 'Schedule next automated threat analysis in 4 hours. Monitor blocked IPs for bypass attempts.',
  timeline: '4 hours'
});

securitySummary.recommendations = recommendations;

// Generate executive summary text
const executiveSummaryText = `
üõ°Ô∏è CYBERSECURITY EXECUTIVE SUMMARY üõ°Ô∏è

üìä THREAT ANALYSIS OVERVIEW:
‚Ä¢ Total Threats Analyzed: ${securitySummary.executive_summary.total_threats_analyzed}
‚Ä¢ Analysis Engine: ${securitySummary.executive_summary.ai_engine}
‚Ä¢ Average Risk Score: ${securitySummary.risk_analysis.average_risk_score}/100

üö® IMMEDIATE ACTIONS TAKEN:
‚Ä¢ Critical Threats Blocked: ${securitySummary.actions_taken.critical_blocks}
‚Ä¢ High-Risk Scheduled: ${securitySummary.actions_taken.high_risk_scheduled}
‚Ä¢ Security Alerts: ${securitySummary.actions_taken.alerts_generated}
‚Ä¢ Monitoring: ${securitySummary.actions_taken.threats_monitored}

‚ö†Ô∏è RISK DISTRIBUTION:
‚Ä¢ Critical (90+): ${securitySummary.risk_analysis.risk_distribution.critical}
‚Ä¢ High (70-89): ${securitySummary.risk_analysis.risk_distribution.high}
‚Ä¢ Medium (50-69): ${securitySummary.risk_analysis.risk_distribution.medium}
‚Ä¢ Low (<50): ${securitySummary.risk_analysis.risk_distribution.low}

üåç TOP THREAT COUNTRIES: ${Object.keys(securitySummary.threat_intelligence.countries_involved).slice(0,3).join(', ')}

üéØ TOP THREAT TYPES: ${Object.keys(securitySummary.threat_intelligence.threat_types).slice(0,3).join(', ')}

Generated: ${reportDate} ${reportTime}
`;

securitySummary.executive_summary_text = executiveSummaryText;

// Console output
console.log("=".repeat(60));
console.log("üõ°Ô∏è  AI CYBERSECURITY SUMMARY REPORT  üõ°Ô∏è");
console.log("=".repeat(60));
console.log(executiveSummaryText);
console.log("=".repeat(60));
console.log(`üìã Full report generated with ${securitySummary.detailed_findings.length} detailed findings`);
console.log(`üîç ${securitySummary.recommendations.length} security recommendations provided`);
console.log("=".repeat(60));

return [{ json: securitySummary }];
